$(document).ready(function () {
    $('.sidenav').sidenav();
    $('.dropdown-trigger').dropdown();
    displayWorkSpace('#menu1', '/api/getTableData');
    $('#menu1').click(function () {
        displayWorkSpace('#menu1', '/api/getTableData');
    });
    $('#menu2').click(function () {
        setActive('#menu2');
        displayWorkSpace('#menu2', '/api/getAnotherTableData');
    });
    $('#menu3').click(function () {
        setActive('#menu3');
        $.getJSON('/api/getRandStr', {}, function (data) {
            $('#workSpace').html('')
                .append("<h5>" + data + "</h5>")
            $.getJSON('/api/getImage', function (data) {
                $('#workSpace').html('')
                    .append(data)
                // $('#workSpace')
                //     .append("<img src='data:image/png;base64,'>")
            });
        })
        $.getJSON('/api/getImage', {eslId:1}, function (data) {
            $('#workSpace').html('')
                .append("<img id='esl-image' src='" + data + "'>")
        });
    });
    $('#menu4').click(function () {
        setActive('#menu4');
        $('#workSpace').html('')
            .append("<h5>sdfsdfsdfsdfsdfsdf</h5>")
    });
});

